import React from "react";
import "../styles/movies.scss";
import moment from 'moment'
import { Link } from 'react-router-dom';

export default class AllMovies extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      movies: [],
      DataisLoaded: false
    };
  }

  componentDidMount() {
    fetch(
      "https://localhost:7063/api/Movie/allmovies")
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          movies: json,
          DataisLoaded: true
        });
      })
  }
  render() {
    const { DataisLoaded, movies } = this.state;
    if (!DataisLoaded) return <div>
      <h1> Pleses wait some time.... </h1> </div>;
    return (
      <div className="container">
        <div className="movies-title">
          <div className="title-block">
            <p className="title">Movies</p>
            <div className="blue-line">
              <div> </div>
            </div>
          </div>
        </div>

        <div className="movies-container" >
        {movies.map((movie) => (
            <div className="movie">
              <div className="image">
                <img src={movie.image} alt="news image" />
              </div>
              <div className="content">
                <div className="title-rating">
                  <h2>Signal</h2>
                </div>
                <div className="movie-text">
                  Ingrid and her siblings are on the run from the Beserkers who have begun invading the villages, taking over <span>..more</span>
                </div>
                <div className="date">
                  <div><span className="dot"></span><p>2022</p></div>
                  <div><span className="dot"></span><p>{movie.duration} min</p></div>
                  <div><span className="dot"></span><p> IMDb <span>8.8</span></p></div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }
}
