import React from "react";
import "../styles/lastNews.scss";
import monkey from "../images/monkey.png";

export default class LastNews extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      news: [],
      DataisLoaded: false
    };
  }

  componentDidMount() {
    fetch(
      "https://localhost:7063/api/News/lastnews")
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          news: json,
          DataisLoaded: true
        });
      })
  }
  render() {
    const { DataisLoaded, news } = this.state;
    if (!DataisLoaded) return <div>
      <h1> Pleses wait some time.... </h1> </div>;
    return (

      <div className="news-block">
        <div className="title-block">
          <p className="title">Latest news</p>
          <div className="blue-line">
            <div> </div>
          </div>
        </div>
        {news.map((news) => (
          <div className="container" key={news.newsId}>
            <div className="content-block">
              <div className="image-section">
                <img src={news.image} alt="news image" />
              </div>
              <div className="text-section">
                <h2 className="title">20th Century Studios President Says More Free Guy Movies Are Coming, and Sequel Script Just 'Days Away'</h2>
                <p className="text">Ryan Reynolds-hit Free Guy looks like it will be getting a sequel sooner than even the actor believed, as a script for Free Guy 2 is almost complete.Ryan Reynolds-hit Free Guy looks like it will be getting a sequel sooner than even the actor believed, as a script for Free Guy 2 is almost complete.</p>
                <div className="info">
                  <p>BY <span>ANTHONY LUID</span></p>
                  <p>3 HOURS AGO</p>
                </div>
              </div>
            </div>
            <hr className="line" />
          </div>
        ))}
      </div>
    )
  }
}
