import "../styles/lastNews.scss";
import moment from "moment";
import { Link } from "react-router-dom";
import useFetch from "../hooks/useFetch";
import { Pagination } from "@mui/material";
import { useState } from "react";

// type NewsPage = {
//   pages: number;
//   currentPage: number;
//   news: News[];
// };

// type News = {
//   id: number;
//   title: string;
//   image: string;
//   content: string;
//   date: string;
//   authorName: string;
// };

export default function NewsPagination() {
  const {
    data,
    error,
    loading,
  } = useFetch<NewsPage[]>(`https://localhost:7063/newspage/${1}?pageSize=${5}`);

let limit = 5;
let currentPage = 1;

  const fetchComments = async () => {
    const res = await fetch(
      `http://localhost:3004/comments?_page=1&_limit=5`
      // `https://jsonplaceholder.typicode.com/comments?_page=${currentPage}&_limit=${limit}`
    );
    const data = await res.json();
    console.log(data);
    return data;
  };

  console.log(fetchComments());

const [items, setitems] = useState([]);

function handleChange(event:any){ 
  console.log(event.currentTarget.textContent);
}

// useEffect(() => {
//   const getNews = async () =>{
//   const res = await fetch(
//     'https://localhost:7063/newspage/1?pageSize=5'
//   );
//   const data = await res.json();
//   setitems(data);
//   console.log("asdasda");
// };
// getNews();
// }, []);

  return (

    <div>
      <Pagination count={5} defaultPage={1} onChange={handleChange} />
    <div className="news-block">
      <div className="title-block">
        <p className="title">News</p>
        <div className="blue-line">
          <div> </div>
        </div>
      </div>
      {/* {loading && <p>Loading data...</p>} */}
      {data && 
          data?.news.map((n) => ( 
          <div className="container" key={n.pages}>
            <div className="content-block">
              <Link to={`/news/${n.id}`}>
                <div className="image-section">
                  <img src={n.image} alt={n.title} />
                </div>
              </Link>
              <div className="text-section">
                <h2 className="title">{n.title}</h2>
                <p className="text">{n.content}</p>
                <div className="info">
                  <p>
                    BY <span>{n.authorName}</span>
                  </p>
                  <p>{moment(n.date).format("MMMM d Y")}</p>
                </div>
              </div>
            </div>
            <hr className="line" />
          </div>
        ))}
      {/* {error && JSON.stringify(error)} */}
    </div>
    </div>

  );
}
