import {
  Avatar,
  Box,
  Button,
  Grid,
  TextField,
  Typography,
} from "@mui/material";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import Container from "@mui/material/Container";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";

type FormTypes = {
  name: string;
  email: string;
  password: string;
  confirmPassword: string;
};

const validationSchema = yup.object({
  name: yup
    .string()
    .required("Name is required")
    .min(3, "Name must containt at least 3 characters")
    .max(20, "Name must contain at most 20 characters"),
  email: yup.string().required("Email is required").email("Invalid mail"),
  password: yup
    .string()
    .required("Password is required")
    .min(6, "Password must containt at least 6 characters")
    .max(15, "Password must containt at most 15 characters"),
  confirmPassword: yup
    .string()
    .oneOf([yup.ref("password"), null], "Password must match"),
});

export default function SignUp() {
  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm<FormTypes>({ resolver: yupResolver(validationSchema) });

  const onSubmit = (values: FormTypes, e:any) => {
    e.target.reset();
    console.log(values);
  };

  return (
    <Container component="main" maxWidth="xs">
      <Box
        sx={{
          marginTop: 8,
          marginBottom: 8,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Avatar sx={{ m: 1, bgcolor: "primary.main" }}></Avatar>
        <Typography component="h1" variant="h5" sx={{ fontWeight: "bold" }}>
          Sign up
        </Typography>
        <Box component="form" onSubmit={handleSubmit(onSubmit)} sx={{ mt: 3 }}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                id="name"
                label="Name"
                {...register("name", {
                  // minLength: {
                  //   value: 3,
                  //   message: "Name must containt at least 3 characters",
                  // },
                  // maxLength: {
                  //   value: 20,
                  //   message: "Name must contain at most 20 characters",
                  // },
                  // required: "Name is required",
                })}
              />
              {errors.name && <p>{errors.name.message}</p>}
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                id="email"
                label="Email Address"
                {...register("email", {
                  // required: "Email is required",
                })}
              />
              {errors.email && <p>{errors.email.message}</p>}
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Password"
                id="password1"
                type="password"
                {...register("password", {
                  // required: "Password is required",
                })}
              />
              {errors.password && <p>{errors.password.message}</p>}
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Confirm password"
                id="password2"
                type="password"
                {...register("confirmPassword", {
                  // required: "Password is required",
                })}
              />
              {errors.confirmPassword && <p>{errors.confirmPassword.message}</p>}
            </Grid>
          </Grid>
          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt: 3, mb: 2 }}
          >
            Sign Up
          </Button>
          <Grid container justifyContent="flex-end">
            <Grid item>
              <Link to="/signin"> Already have an account? Sign in </Link>
            </Grid>
          </Grid>
        </Box>
      </Box>
    </Container>
  );
}
