import React from "react";
import "../styles/lastNews.scss";
import moment from 'moment'
import { Link } from 'react-router-dom';
import { useEffect, useState } from "react";

type News = {
  newsId: number,
  title: string,
  image: string,
  content:string,
  date: string;
  authorFirstName: string,
  authorLastName: string
};

export default function LastNews(){
  const [news, setNews] = useState<News[] | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);


  useEffect(() =>{
    async function fetchPosts(){
      const response = await fetch("https://localhost:7063/api/lastnews");
      setLoading(false);
      const result = await response.json();
      if(response.status === 404){
        result
      }
      setNews(result);
    }
    fetchPosts();
  }, []);

    return (
      <div className="news-block">
        <div className="title-block">
          <p className="title">Latest news</p>
          <div className="blue-line">
            <div> </div>
          </div>
        </div>
        {news && news.map((n) => (
          <div className="container" key={n.newsId}>
            <div className="content-block">
              <Link to={`/news/${n.newsId}`}>
                <div className="image-section">
                  <img src={n.image} alt={n.title} />
                </div>
              </Link>
              <div className="text-section">
                <h2 className="title">{n.title}</h2>
                <p className="text">{n.content}</p>
                <div className="info">
                  <p>BY <span>{n.authorFirstName + " " + n.authorLastName}</span></p>
                  <p>{moment(n.date).format('MMMM d')}</p>
                </div>
              </div>
            </div>
            <hr className="line" />
          </div>
        ))}
      </div>
    )
}
