import React, { Component } from "react";
import "../styles/newsdetails.scss";
import moment from 'moment'
import useFetch from "../hooks/useFetch";


type SingleNews = {
  id: number,
  title: string,
  image: string,
  content:string,
  date: string;
  AuthorName: number;
};

export default function SingleNews() {

  const {
    data : news,
    error,
    loading,
  } = useFetch<SingleNews[]>("https://localhost:7063/api/news/" + window.location.pathname.substring(window.location.pathname.lastIndexOf('/') + 1));

    return (
      <div className="container">
        <div className="single-news">
          <div className="title">
            <p>{JSON.stringify(news.title)}</p>
          </div>

          <div className="info">
            <p>BY <span>{data.authorName}</span></p>
            <p>{moment(data.date).format('MMMM d')}</p>
          </div>

          <div className="image">
            <img src={data.image} alt={data.title} />
          </div>
          <div className="content">
            <p>{data.content}</p>
          </div>
        </div>
      </div>
    );
  }
