import React from "react";
import "../styles/lastNews.scss";
import monkey from "../images/monkey.png";
import moment from 'moment'

export default class AllNews extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      news: [],
      DataisLoaded: false
    };
  }

  componentDidMount() {
    fetch(
      "https://localhost:7063/api/News/allnews")
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          news: json,
          DataisLoaded: true
        });
      })
  }
  render() {
    const { DataisLoaded, news } = this.state;
    if (!DataisLoaded) return <div>
      <h1> Pleses wait some time.... </h1> </div>;

  return (
    <div className="news-block">
        <div className="title-block">
          <p className="title">Latest news</p>
          <div className="blue-line">
            <div> </div>
          </div>
        </div>
        {news.map((news) => (
          <div className="container" key={news.newsId}>
            <div className="content-block">
              <div className="image-section">
                <img src={news.image} alt={news.title} />
              </div>
              <div className="text-section">
                <h2 className="title">{news.title}</h2>
                <p className="text">{news.content}</p>
                <div className="info">
                  <p>BY <span>{news.author}</span></p>
                  <p>{moment(news.date).format('MMMM d YYYY')}</p>
                </div>
              </div>
            </div>
            <hr className="line" />
          </div>
        ))}
      </div>
  )
}
}
