import { yupResolver } from "@hookform/resolvers/yup";
import { FormControl, InputLabel, Input, FormHelperText } from "@mui/material";
import { useState } from "react";
import { useForm } from "react-hook-form";
import { Redirect } from "react-router-dom";
import { addNewsSchema } from "../validations/addNewsValidation";

type NewsType = {
  title: string;
  image: string;
  content: string;
  date: string;
  authorId: number;
};

export default function AddNews() {
  const [redirect, setRedirect] = useState(false);

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<NewsType>({ resolver: yupResolver(addNewsSchema) });

  const onSubmit = async (values: NewsType) => {
    console.log(values);

    const url = "http://localhost:7063/api/register";
    const data = {
      title: values.title,
      image: values.image,
      content: values.content,
      date: values.date,
      authorId: values.authorId,
    };

    try {
      const response = await fetch(url, {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
          "Content-Type": "application/json",
        },
      });
      const json = (await response).json();
      console.log("Succes:", JSON.stringify(json));
    } catch (error) {
      console.error("Error:", error);
    }
    setRedirect(true);
  };

  if (redirect) {
    return <Redirect to="/news" />;
  }

  return (
    <Container>
      <FormControl>
        <InputLabel htmlFor="my-input">Email address</InputLabel>
        <Input id="my-input" aria-describedby="my-helper-text" />
        <FormHelperText id="my-helper-text">
          We'll never share your email.
        </FormHelperText>
      </FormControl>
    </Container>
  );
}
