import { Redirect } from "react-router-dom";
import useFetch from "../hooks/useFetch";
import { NewsPage } from "../Types/Types";

export async function DeleteNews(id: number) {
  // await fetch(`http://localhost:7063/api/deletenews/${id}`,{
  //   method: 'DELETE'
  // })
  const [page, setPage] = useState(1);

  const url = `http://localhost:7063/newspage/${page}?pageSize=${5}`;

  const { data, error, loading } = useFetch<NewsPage>(url);

    fetch(`http://localhost:7063/api/deletenews/${id}`, { 
      method: "DELETE" })
      .then(async (response) => {
        if (!response.ok) {
          const error =  response.status;
          return Promise.reject(error);
        }
      })
      .catch((error) => {
        console.warn("There was an error!", error);
      });

  return(
    <>{loading && <p>Loading data...</p>}</>
  )
}
