import { yupResolver } from "@hookform/resolvers/yup";
import { Container, Box, Grid, TextField, Typography } from "@mui/material";
import { useForm } from "react-hook-form";
import { Review } from "../../Types/Types";
import { addNewsSchema } from "../../validations/addNewsValidation";

export function AddReview() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<Review>({ resolver: yupResolver(addNewsSchema) });

  const onSubmit = async (values: any) => {
    const url = "http://localhost:7063/addreview";
    const data = {
      content: values.content,
      authorId: values.authorId,
    };
    try {
      await fetch(url, {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
          "Content-Type": "application/json",
        },
      });
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <Container>
      <Box
        component="form"
        onSubmit={handleSubmit(onSubmit)}
        sx={{ display: "block", mt: 4, mb: 4 }}
      >
        <Grid container spacing={2} sx={{ display: "block" }}>
          <Grid item xs={6}>
            <TextField
              fullWidth
              id="Content"
              label="Review content"
              {...register("content", {})}
            />
            {errors.title && (
              <Typography style={{ color: "#F95252" }}>
                * {errors.title.message}
              </Typography>
            )}
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
}
