import { useEffect, useState } from "react";
import useFetch from "../../hooks/useFetch";
import { NewsPage, NewsType } from "../../Types/Types";

export default async function deleteNews(id:number) {
  const [page, setPage] = useState(1);

  const newsUrl = `http://localhost:7063/newspage/${page}?pageSize=${5}`;

  const { refetch } = useFetch<NewsPage>(newsUrl);

  const url = `http://localhost:7063/api/deletenews/${id}`;
  await fetch(url, {
    method: "DELETE",
  })
    .then(async (response) => {
      if (response.ok) {
        refetch
      }
    })
    .catch((error) => {
      console.warn("There was an error!", error);
    });
}

