import { yupResolver } from "@hookform/resolvers/yup";
import {
  Container,
  Box,
  Grid,
  TextField,
  Typography,
  Button,
} from "@mui/material";
import { useForm } from "react-hook-form";
import { Review } from "../../Types/Types";
import { addNewsSchema } from "../../validations/addNewsValidation";

export function AddReview() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<Review>({ resolver: yupResolver(addNewsSchema) });

  const onSubmit = async (values: any) => {
    console.log("Hello");
    const url = "http://localhost:7063/api/addreview";
    const data = {
      user: "Gheorghe",
      content: values.content,
      movieId: 1,
    };
    try {
      await fetch(url, {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
          "Content-Type": "application/json",
        },
      });
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <form
      onSubmit={handleSubmit(onSubmit)}
      // sx={{ display: "block", mt: 4, mb: 4 }}
    >
      <Grid container spacing={2} sx={{ display: "block" }}>
        <Grid item xs={6}>
          <TextField
            fullWidth
            id="Content"
            label="Review content"
            {...register("content", {})}
          />
          {errors.content && (
            <Typography style={{ color: "#F95252" }}>
              * {errors.content.message}
            </Typography>
          )}
        </Grid>
        <Button type="submit" variant="contained" sx={{ mt: 3, ml: 2 }}>
          Add review
        </Button>
      </Grid>
    </form>
  );
}
