import "../styles/lastNews.scss";
import moment from "moment";
import { Link } from "react-router-dom";
import useFetch from "../hooks/useFetch";
import { Pagination } from "@mui/material";
import { useEffect, useState } from "react";

export default function NewsPagination() {
  const [items, setItems] = useState([]);
  const [page, setpage] = useState(1);

  // useEffect(() => {
  //   const getNews = async () =>{
  //   const res = await fetch(
  //     `https://localhost:7063/newspage/${1}?pageSize=${5}`
  //   );
  //   const data = await res.json();
  //   setItems(data);
  // };
  // getNews();
  // }, []);

  const fetchPagedNews = async (currentPage: number) => {
    const res = await fetch(
      `https://localhost:7063/newspage/${currentPage}?pageSize=${5}`
    );
    const data = await res.json();
    console.log(data);

    return data;
  };

  async function handleChange(event: any) {
    console.log(event.currentTarget.textContent);

    let currentPage = event.currentTarget.textContent;
    const dataFromServer = await fetchPagedNews(currentPage);
    setItems(dataFromServer);
  }

  return (
    <div>
      <Pagination count={5} defaultPage={page} onChange={handleChange} />
      <div className="news-block">
        <div className="title-block">
          <p className="title">News</p>
          <div className="blue-line">
            <div> </div>
          </div>
        </div>
        { items?.news.map((n) => ( 
          <div className="container" key={n.pages}>
            <div className="content-block">
              <Link to={`/news/${n.id}`}>
                <div className="image-section">
                  <img src={n.image} alt={n.title} />
                </div>
              </Link>
              <div className="text-section">
                <h2 className="title">{n.title}</h2>
                <p className="text">{n.content}</p>
                <div className="info">
                  <p>
                    BY <span>{n.authorName}</span>
                  </p>
                  <p>{moment(n.date).format("MMMM d Y")}</p>
                </div>
              </div>
            </div>
            <hr className="line" />
          </div>
        ))}
      </div>
    </div>
  );
}
