import "../styles/lastNews.scss";
import moment from "moment";
import { Link } from "react-router-dom";
import useFetch from "../hooks/useFetch";
import { Pagination } from "@mui/material";
import { useEffect, useState } from "react";

type NewsPagination = {
  newsList: News[];
  pages: number;
  currentPage: number;
};

type News = {
  id: number;
  title: string;
  image: string;
  content: string;
  date: string;
  authorName: string;
};

export default function NewsPagination() {
  const {
    data,
    error,
    loading,
  } = useFetch<NewsPagination[]>("https://localhost:7063/newspage/1?pageSize=5");

const [items, setitems] = useState([]);

function handleChange(event:any){ 
  console.log(event.currentTarget.textContent);
}

// useEffect(() => {
//   const getNews = async () =>{
//   const res = await fetch(
//     'https://localhost:7063/newspage/1?pageSize=5'
//   );
//   const data = await res.json();
//   setitems(data);
//   console.log("asdasda");
// };
// getNews();
// }, []);

  return (

    <div>
      <Pagination count={10} defaultPage={1} onChange={handleChange} />
    <div className="news-block">
      <div className="title-block">
        <p className="title">News</p>
        <div className="blue-line">
          <div> </div>
        </div>
      </div>
      {/* {loading && <p>Loading data...</p>} */}
      { data?.data.map((item) => (
          <div className="container" key={item.id}>
            <div className="content-block">
              <Link to={`/news/${item.id}`}>
                <div className="image-section">
                  <img src={item.image} alt={item.title} />
                </div>
              </Link>
              <div className="text-section">
                <h2 className="title">{item.title}</h2>
                <p className="text">{item.content}</p>
                <div className="info">
                  <p>
                    BY <span>{item.authorName}</span>
                  </p>
                  <p>{moment(item.date).format("MMMM d Y")}</p>
                </div>
              </div>
            </div>
            <hr className="line" />
          </div>
        ))}
      {/* {error && JSON.stringify(error)} */}
    </div>
    </div>

  );
}
