import "../styles/lastNews.scss";
import moment from 'moment'
import { Link } from 'react-router-dom';
import { useEffect, useState } from "react";
import useFetch from "../hooks/useFetch";

type News = {
  newsId: number,
  title: string,
  image: string,
  content:string,
  date: string;
  authorFirstName: string,
  authorLastName: string
};

