import React from "react";
import "../styles/lastMovies.scss";
import movie from "../images/movie.png";
import moment from 'moment'

// export default function LatestMovies() {
export default class LastMovies extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      items: [],
      DataisLoaded: false
    };
  }

  componentDidMount() {
    fetch(
      "https://localhost:7063/api/Movie/allmovies")
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          items: json,
          DataisLoaded: true
        });
      })
  }

  getDate = (date) => {
    return date.split('-',2)
   }

  render() {
    const { DataisLoaded, items } = this.state;
    if (!DataisLoaded) return <div>
      <h1> Pleses wait some time.... </h1> </div>;

    return (
      <div className="container">
        <div className="movies-title">
          <div className="title-block">
            <p className="title">Movies</p>
            <div className="blue-line">
              <div> </div>
            </div>
          </div>
        </div>

        <div className="movies-block">
          {items.map((item) => (
            <div className="movie" key={item.movieId}>
              <div className="image">
                <img src={item.image} alt="news image" />
              </div>
              <div className="content">
              <div className="title-rating">
                <h2>{item.title}</h2>
                <p> IMDb <span>{item.rating}</span></p>
              </div>
              <div className="movie-text">
                {item.description} <span>..more</span>
              </div>
              <div className="date">
                <p>{moment(item.release).format('MM')}</p>
              </div>
            </div>
            </div>
          ))}
          {/* <div className="movie">
            <div className="image">
              <img src={movie} alt="news image" />
            </div>
            <div className="content">
              <div className="title-rating">
                <h2>Signal</h2>
                <p> IMDb <span>8.8</span></p>
              </div>
              <div className="movie-text">
                Ingrid and her siblings are on the run from the Beserkers who have begun invading the villages, taking over <span>..more</span>
              </div>
              <div className="date">
                <p>January 14, 2022</p>
              </div>
            </div>
          </div> */}
        </div>

        <div className="movie-button">
          <button>
            <p>View all movies</p>
          </button>
        </div>
      </div>
    );
  }
}
