import React from "react";
import "../styles/lastNews.scss";
import moment from 'moment'
import { Link } from 'react-router-dom';
import { useEffect, useState } from "react";

type News = {
  newsId: number,
  title: string,
  image: string,
  content:string,
  date: string;
  authorFirstName: string,
  authorLastName: string
};

export default function LastNews(){
  const [news, setNews] = useState<News[] | null>(null);

  useEffect(() =>{
    async function fetchPosts(){
      const response = await fetch("https://localhost:7063/api/lastnews");
      const result = await response.json();
      setNews(result);
    }
    fetchPosts();
  }, []);

  return(
    <div>
      {!news && <p>No news...</p>}
      {/* {news && news.map(n =>
        <div key={n.newsId}>
            <p>n.title</p>
        </div>
        )} */}
//       <div className="news-block">
//         <div className="title-block">
//           <p className="title">Latest news</p>
//           <div className="blue-line">
//             <div> </div>
//           </div>
//         </div>
//         {news.map((news) => (
          <div className="container" key={news.newsId}>
            <div className="content-block">
              <Link to={`/news/${news.newsId}`}>
                <div className="image-section">
                  <img src={news.image} alt={news.title} />
                </div>
              </Link>
              <div className="text-section">
                <h2 className="title">{news.title}</h2>
                <p className="text">{news.content}</p>
                <div className="info">
                  <p>BY <span>{news.authorFirstName + " " + news.authorLastName}</span></p>
                  <p>{moment(news.date).format('MMMM d')}</p>
                </div>
              </div>
            </div>
            <hr className="line" />
          </div>
        ))}
      </div>

    </div>
  );
}

