import "../styles/lastNews.scss";
import moment from "moment";
import { Link } from "react-router-dom";
import useFetch from "../hooks/useFetch";
import { Pagination } from "@mui/material";
import { useEffect, useState } from "react";

type NewsPage = {
  pages: number;
  currentPage: number;
  news: News[];
};

type News = {
  id: number;
  title: string;
  image: string;
  content: string;
  date: string;
  authorName: string;
};

export default function NewsPagination() {
  const {
    data: news,
    error,
    loading,
  } = useFetch<NewsPage[]>("https://localhost:7063/newspage/1?pageSize=5");

console.log(news);

const [items, setitems] = useState([]);

function handleChange(event:any){ 
  console.log(event.currentTarget.textContent);
}

// useEffect(() => {
//   const getNews = async () =>{
//   const res = await fetch(
//     'https://localhost:7063/newspage/1?pageSize=5'
//   );
//   const data = await res.json();
//   setitems(data);
//   console.log("asdasda");
// };
// getNews();
// }, []);

  return (

    <div>
      <Pagination count={5} defaultPage={1} onChange={handleChange} />
    <div className="news-block">
      <div className="title-block">
        <p className="title">News</p>
        <div className="blue-line">
          <div> </div>
        </div>
      </div>
      {/* {loading && <p>Loading data...</p>} */}
      {news && 
          news?.news.map((news) => ( 
          <div className="container" key={news.id}>
            <div className="content-block">
              <Link to={`/news/${news.id}`}>
                <div className="image-section">
                  <img src={news.image} alt={news.title} />
                </div>
              </Link>
              <div className="text-section">
                <h2 className="title">{news.title}</h2>
                <p className="text">{news.content}</p>
                <div className="info">
                  <p>
                    BY <span>{news.authorName}</span>
                  </p>
                  <p>{moment(news.date).format("MMMM d Y")}</p>
                </div>
              </div>
            </div>
            <hr className="line" />
          </div>
        ))}
      {/* {error && JSON.stringify(error)} */}
    </div>
    </div>

  );
}
