import React from "react";
import "../styles/lastNews.scss";
import moment from 'moment'
import { Link } from 'react-router-dom';
import useFetch from "../hooks/useFetch";

export default function AllNews() {

  type News = {
    newsId: number,
    title: string,
    image: string,
    content:string,
    date: string;
    authorFirstName: string,
    authorLastName: string
  };
  const {data: news, error, loading} = useFetch<News[]>("https://localhost:7063/api/lastnews")
  return (
    <div className="news-block">
        <div className="title-block">
          <p className="title">Latest news</p>
          <div className="blue-line">
            <div> </div>
          </div>
        </div>
        {news.map((news) => (
          <div className="container" key={news.newsId}>
            <div className="content-block">
            <Link to={`/news/${news.newsId}`}>
              <div className="image-section">
                <img src={news.image} alt={news.title} />
              </div>
              </Link>
              <div className="text-section">
                <h2 className="title">{news.title}</h2>
                <p className="text">{news.content}</p>
                <div className="info">
                  <p>BY <span>{news.author}</span></p>
                  <p>{moment(news.date).format('MMMM d Y')}</p>
                </div>
              </div>
            </div>
            <hr className="line" />
          </div>
        ))}
      </div>
  )
}
