import { yupResolver } from "@hookform/resolvers/yup";
import {
  FormControl,
  InputLabel,
  Input,
  FormHelperText,
  Container,
  Grid,
  TextField,
  Typography,
  Box,
  Button,
} from "@mui/material";
import { useState } from "react";
import { useForm } from "react-hook-form";
import { Redirect } from "react-router-dom";
import { addNewsSchema } from "../validations/addNewsValidation";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import moment from "moment";

type NewsType = {
  title: string;
  image: string;
  content: string;
  date: string;
  authorId: number;
};

export default function AddNews() {
  const [redirect, setRedirect] = useState(false);
  const [dateValue, setDateValue] = useState<Date | null>(null);

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<NewsType>({ resolver: yupResolver(addNewsSchema) });

  const onSubmit = async (values: any) => {
    const url = "http://localhost:7063/addnews";
    const data = {
      title: values.title,
      image: values.image.event.target.files[0].name,
      content: values.content,
      date: moment(dateValue).format("YYYY-MM-DD"),
      // date: values.date,
      authorId: values.authorId,
    };
    console.log(data);

    try {
      const response = await fetch(url, {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
          "Content-Type": "application/json",
        },
      });
    } catch (error) {
      console.error("Error:", error);
    }
    setRedirect(true);
  };

  if (redirect) {
    return <Redirect to="/news" />;
  }

  return (
    <Container>
      <Box component="form" onSubmit={handleSubmit(onSubmit)} sx={{ mt: 3 }}>
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              fullWidth
              id="title"
              label="News title"
              {...register("title", {})}
            />
            {errors.title && (
              <Typography style={{ color: "#F95252" }}>
                * {errors.title.message}
              </Typography>
            )}
          </Grid>
          <Grid item xs={6}>
           <input type="file" {...register("image")}></input>
            {errors.image && (
              <Typography style={{ color: "#F95252" }}>
                * {errors.image.message}
              </Typography>
            )}
          </Grid>
          <Grid item xs={6}>
            <TextField
              fullWidth
              id="content"
              label="News content"
              {...register("content", {})}
            />
            {errors.content && (
              <Typography style={{ color: "#F95252" }}>
                * {errors.content.message}
              </Typography>
            )}
          </Grid>

          <Grid item xs={6}>
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                label="News Date"
                value={dateValue}
                onChange={(newValue) => {
                  setDateValue(newValue);
                }}
                renderInput={(register) => <TextField {...register} name='date' />}
              />
            </LocalizationProvider>

            {/* <TextField
              fullWidth
              id="date"
              label="News date"
              {...register("date", {})}
            /> */}

            {/* <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                label="News Date"
                value={dateValue}
                onChange={(newValue) => {
                  setDateValue(newValue);
                }}
                renderInput={(register) => (
                  <TextField {...register} name="date" />
                )}
              />
            </LocalizationProvider> */}
          </Grid>

          <Grid item xs={6}>
            <TextField
              fullWidth
              id="authorId"
              label="Author id"
              {...register("authorId", {})}
            />
            {errors.authorId && (
              <Typography style={{ color: "#F95252" }}>
                * {errors.authorId.message}
              </Typography>
            )}
          </Grid>
          <Button type="submit" variant="contained" sx={{ mt: 3, ml: 2 }}>
            Sign Up
          </Button>
        </Grid>
      </Box>
    </Container>
  );
}
