import "../../styles/moviedetails.scss";
import moment from "moment";
import useFetch from "../../hooks/useFetch";
import { MovieType } from "../../Types/Types";
import { useState } from "react";
import { AddReview } from "./AddReview";
import { TextField, Typography } from "@mui/material";

export default function MovieDetails() {
  const [open, setOpen] = useState(false);
  const url =
    "http://localhost:7063/api/movies/" +
    window.location.pathname.substring(
      window.location.pathname.lastIndexOf("/") + 1
    );
  const { data: movie, error, loading } = useFetch<MovieType>(url);

  let reviewContent;
  const userRole = localStorage.getItem('role');

  if(userRole === 'admin'){
    console.log("adminHere");
    reviewContent = (
      <AddReview open={open} setOpen={setOpen} />
    )
  }else{
    console.log("user")
    reviewContent = (
      <Typography style={{ color: "#F95252" }}>* You need to sign in first!</Typography>
    )
  }
  return (
    <div className="container">
      {loading && <p>Loading data...</p>}
      <div className="single-block">
        <div className="image-block">
          <img src={movie?.image} alt={movie?.title} />
        </div>
        <div className="details-container">
          <h2>{movie?.title}</h2>
          <div className="date">
            <div>
              <p>{moment(movie?.release).format("YYYY")}</p>
            </div>
            <div>
              <p>{movie?.duration} min</p>
            </div>
            <div>
              <p>
                IMDb <span>{movie?.rating}</span>
              </p>
            </div>
          </div>
          <p className="description">{movie?.description}</p>
          <div className="details">
            <div className="names">
              <p>Country:</p>
              <p>Genre:</p>
              <p>Actors:</p>
              <p>Budget:</p>
            </div>

            <div className="dates">
              <p>{movie?.country}</p>
              <p>{movie?.genres.join(", ")}</p>
              <p>{movie?.actors.join(", ")}</p>
              <p>${movie?.budget} mln</p>
            </div>
          </div>
        </div>
      </div>

      <div className="reviews-container">
        <h2>Reviews</h2>
        <div className="button">
          <button onClick={() => setOpen(!open)}>
            <p>Add review</p>
          </button>
        </div>
        {/* <div>{open && <AddReview open={open} setOpen={setOpen} />}</div> */}
        <div>{open && reviewContent}</div>

        {movie &&
          movie?.reviews
            .map((review) => (
              <div className="review" key={review.content}>
                <div className="user">
                  <p>{review.user}</p>
                  <div>
                    <span className="dot"></span>
                    <p>{moment(review.date).format("LLL")}</p>
                  </div>
                </div>
                <div className="text">
                  <p>{review.content}</p>
                </div>
              </div>
            ))
            .reverse()}
      </div>
      {error && JSON.stringify(error)}
    </div>
  );
}
