import { yupResolver } from "@hookform/resolvers/yup";
import {
  FormControl,
  InputLabel,
  Input,
  FormHelperText,
  Container,
  Grid,
  TextField,
  Typography,
  Box,
  Button,
} from "@mui/material";
import { useState } from "react";
import { useForm } from "react-hook-form";
import { Redirect } from "react-router-dom";
import { addNewsSchema } from "../validations/addNewsValidation";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";

type NewsType = {
  title: string;
  image: string;
  content: string;
  date: string;
  authorId: number;
};

export default function AddNews() {
  const [redirect, setRedirect] = useState(false);
  const [dateValue, setDateValue] = useState<Date | null>(null);

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<NewsType>({ resolver: yupResolver(addNewsSchema) });

  const onSubmit = async (values: NewsType) => {
    console.log(values);

    const url = "http://localhost:7063/addnews";
    const data = {
      title: values.title,
      image: values.date,
      content: values.content,
      date: dateValue,
      authorId: values.authorId,
    };
    console.log("val " + imageValue);

    try {
      const response = await fetch(url, {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
          "Content-Type": "application/json",
        },
      });
      const json = response.json();
      console.log("Succes:", JSON.stringify(json));
    } catch (error) {
      console.error("Error:", error);
    }
    setRedirect(true);
  };

  if (redirect) {
    return <Redirect to="/news" />;
  }

  return (
    <Container>
      <Box component="form" onSubmit={handleSubmit(onSubmit)} sx={{ mt: 3 }}>
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              fullWidth
              id="title"
              label="News title"
              {...register("title", {})}
            />
            {errors.title && (
              <Typography style={{ color: "#F95252" }}>
                * {errors.title.message}
              </Typography>
            )}
          </Grid>
          <Grid item xs={6}>
            <TextField
              fullWidth
              id="image"
              label="News image"
              {...register("image", {})}
            />
            {errors.image && (
              <Typography style={{ color: "#F95252" }}>
                * {errors.image.message}
              </Typography>
            )}
          </Grid>
          <Grid item xs={6}>
            <TextField
              fullWidth
              id="content"
              label="News content"
              {...register("content", {})}
            />
            {errors.content && (
              <Typography style={{ color: "#F95252" }}>
                * {errors.content.message}
              </Typography>
            )}
          </Grid>

          <Grid item xs={6}>
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                label="Basic example"
                value={imageValue}
                onChange={(newValue) => {
                  setImageValue(newValue);
                }}
                renderInput={(register) => <TextField {...register} name='date' />}
              />
            </LocalizationProvider>
          </Grid>

          <Grid item xs={6}>
            <TextField
              fullWidth
              id="authorId"
              label="Author id"
              {...register("authorId", {})}
            />
            {errors.authorId && (
              <Typography style={{ color: "#F95252" }}>
                * {errors.authorId.message}
              </Typography>
            )}
          </Grid>
          <Button type="submit" variant="contained" sx={{ mt: 3, ml: 2 }}>
            Sign Up
          </Button>
        </Grid>
      </Box>
    </Container>
  );
}
